import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Link } from 'react-router-dom';

import Home from './Home';
import About from './About';
import Contact from './Contact';

class Nav extends Component {
    constructor(props) {
        super(props);

        this.state = {
            condition: false 
        };

        this.toggleMenu = this.toggleMenu.bind(this);
    }

    toggleMenu() {
        this.setState({condition: !this.state.condition})
    }
  render() {
    return (
        <Router>
        <div>
            <button style={{marginLeft: '100px'}} type="button"  className="btn btn-dark" onClick={ this.toggleMenu } >Menu</button>
            <nav className= { this.state.condition ? " navbar-light bg-light fixed-top toggled" : "navbar-light bg-light fixed-top" }>
                <a className="navbar-brand" href="/">Navbar</a>
                <ul className="nav flex-column" id="navbarNav">
                <li><Link to={'/'} className="nav-link"> Home </Link></li>
                <li><Link to={'/about'} className="nav-link"> About </Link></li>
                <li><Link to={'/contact'} className="nav-link"> Contact </Link></li>
                </ul>
            </nav>
      </div>
      <Switch>
              <Route exact path='/' component={Home} />
              <Route path='/contact' component={Contact} />
              <Route path='/about' component={About} />
          </Switch>
      </Router>
    );
  }
}

export default Nav;
