                    </ul>
                </li>
                <li className="maninNavUlWrap"><Link to={'/contact'} className='nav-link topLi'  onClick={this.active.bind(this, 2)}> Contact<span className="line"></span> </Link>
                <ul>
                    {this.state.liContacts.map((liContact, index) =>
