import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

import Home from './Home';
import About from './About';
import Contact from './Contact';
import HomeOne from './home_sub/Home_One';

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            condition: false,
            conditionTwo: false,
            isActive: 0,
            NavText: 'Home'
        };

        this.toggleMenu = this.toggleMenu.bind(this);
        this.toggleMenu2 = this.toggleMenu2.bind(this);
        this.toggleInnerMenu = this.toggleInnerMenu.bind(this);
        this.active = this.active.bind(this)

    }

    toggleMenu() {
        this.setState({condition: true});
    }
    toggleInnerMenu() {
        this.setState({conditionTwo: !this.state.conditionTwo})
    }
    toggleMenu2() {
        this.setState({condition: false});
    }
    active(index) {
        this.setState({ isActive: index });
        if(this.state.isActive===0){
            this.setState({NavText: "Home"})
        }
        if(this.state.isActive===1){
            this.setState({NavText: "About"})
        }
        if(this.state.isActive===2){
            this.setState({NavText: "Contact"})
        }
    }

  render() {

    return (

        <Router>
        <header className="bg-light">
        <div>
            <div id="mainNavOverlay" className= { this.state.condition ?  'show' : "hide" }></div>
            <button style={{position: 'relative',top: '6px',left: '10px',zIndex: '3'}} type="button"  className="btn menuToggle" ref={button => {this.MainNavName = button;}} onMouseEnter={ this.toggleMenu } >{this.state.NavText}</button>
            <nav className= { this.state.condition ? " navbar-light bg-light fixed-top" : "navbar-light bg-light fixed-top toggled" } onMouseLeave={ this.toggleMenu2 }>
                <ul className="nav" id="navbarNav">
                <li className="maninNavUlWrap"><Link to={'/'} className={this.state.isActive===0 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 0).toggleInnerMenu} > Home </Link>
                    <ul>
                        <li><Link to={'/Home_Page/Home_One'} className={this.state.isActive===0.1 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 0.1 ).toggleInnerMenu} > Home 1 </Link></li>
                        <li><Link to={'/Home_Page/Home_One'} className={this.state.isActive===0.2 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 0.2 ).toggleInnerMenu} > Home 1 </Link></li>
                    </ul>
                </li>
                <li className="maninNavUlWrap"><Link to={'/about'} className={this.state.isActive===1 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 1).toggleInnerMenu}> About </Link>
                    <ul>
                        <li><Link to={'/Home_Page/Home_One'} className={this.state.isActive===1 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 1).toggleInnerMenu} > Home 2 </Link></li>
                    </ul>
                </li>
                <li className="maninNavUlWrap"><Link to={'/contact'} className={this.state.isActive===2 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 2).toggleInnerMenu}> Contact </Link>
                    <ul>
                        <li><Link to={'/Home_Page/Home_One'} className={this.state.isActive===2 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 2).toggleInnerMenu} > Home 3 </Link></li>
                    </ul>
                </li>
                </ul>
            </nav>

            <div className="innerNav"> 
                <nav className={this.state.conditionTwo ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/'} className='nav-link'> Home </Link></li>
                            <ul>
                            <li><Link to={'/Home_Page/Home_One'} className='nav-link'> Home One</Link></li>
                            <li><Link to={'/Home_Page/Home_One'} className='nav-link'> Home One</Link></li>
                            <li><Link to={'/Home_Page/Home_One'} className='nav-link'> Home One</Link></li>
                            </ul>
                    </ul>
                </nav>
                <nav className={this.state.conditionTwo ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/about'} className='nav-link'>About</Link></li>
                            <ul>
                            <li><Link to={'/Home_Page/Home_One'} className='nav-link'> Home Two</Link></li>
                            </ul>
                    </ul>
                </nav>
                <nav className={this.state.conditionTwo ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/contact'} className='nav-link'>Contact</Link></li>
                            <ul>
                            <li><Link to={'/Home_Page/Home_One'} className='nav-link'> Home Three</Link></li>
                            </ul>
                    </ul>
                </nav>
            </div>

      </div>
      </header>
      <Switch>
              <Route exact path='/' component={Home} />
              <Route path='/contact' component={Contact} />
              <Route path='/about' component={About} />
              <Route path='/Home_Page/Home_One' component={HomeOne} />
          </Switch>
      </Router>

    );
  }
}

export default Header;
