import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

import Home from './Home';
import About from './About';
import Contact from './Contact';
import HomeOne from './Home_Page/Home_One';

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            condition: false,
            isActive: 0,
            name: ''
        };

        this.toggleMenu = this.toggleMenu.bind(this);
        this.toggleMenu2 = this.toggleMenu2.bind(this);
        this.active = this.active.bind(this)
    }

    toggleMenu() {
        this.setState({condition: true})
    }
    toggleMenu2() {
        this.setState({condition: false})
    }
    active(index) {
        this.setState({ isActive: index })
    }

  render() {
    if(this.state.isActive===0){
       this.setState({name: 'Home'})
     }
     if(this.state.isActive===1){
         this.setState({name: 'About'})
     }
     if(this.state.isActive===2){
          this.setState({name: 'Contact'})
     }
    return (

        <Router>
        <header className="bg-light">
        <div>
            <button style={{position: 'relative',top: '6px',left: '10px'}} type="button"  className="btn menuToggle" onMouseEnter={ this.toggleMenu } >Menu</button>
            <nav className= { this.state.condition ? " navbar-light bg-light fixed-top " : "navbar-light bg-light fixed-top toggled" } onMouseLeave={ this.toggleMenu2 }>
                <a className="navbar-brand" href="/">Navbar</a>
                <ul className="nav" id="navbarNav">
                <li><Link to={'/'} className={this.state.isActive===0 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 0)} > Home </Link>
                    <ul>
                        <li><Link to={'/Home_Page/Home_One'} className="nav-link"> Home 1 </Link></li>
                    </ul>
                </li>
                <li><Link to={'/about'} className={this.state.isActive===1 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 1)}> About </Link></li>
                <li><Link to={'/contact'} className={this.state.isActive===2 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 2)}> Contact </Link></li>
                </ul>
            </nav>

            <div className="innerNav"> 
                <p>{this.setState({name: ''})}</p>
                <nav className={this.state.isActive===1 ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li><Link to={'/Home_Page/Home_One'} className='nav-link'> Home One</Link></li>
                    </ul>
                </nav>
            </div>

      </div>
      </header>
      <Switch>
              <Route exact path='/' component={Home} />
              <Route path='/contact' component={Contact} />
              <Route path='/about' component={About} />
              <Route path='/Home_Page/Home_One' component={HomeOne} />
          </Switch>
      </Router>

    );
  }
}

export default Header;
