import React, { Component } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import Table from "../components/Table";
import AgentEdit from "./Agent_Edit";

const postsRow = [
  {
    id: 0,
    Project: "Project Name",
    Agent: "Steve",
    Type: "Admin"
  },
  {
    id: 1,
    Project: "Project Name",
    Agent: "Sam",
    Type: "Read Only"
  },
  {
    id: 2,
    Project: "Project Name",
    Agent: "Shawn",
    Type: "Prime Contractor"
  }
];
const postHead = [
  { title: "ID" },
  { title: "Project Name" },
  { title: "Agent" },
  { title: "Permission Type" }
];
function searchFilter(search) {
  return function(x) {
    return x.Agent.toLowerCase().includes(search.toLowerCase()) || !search;
  };
}

class Agents extends Component {
  constructor(props) {
    super(props);
    this.state = {
      condition: false,
      showRowCondition: false,
      showRowFilter: false,
      showRowCard: false,
      showSearch: false,
      postsRow: postsRow,
      postHead: postHead,
      search: ""
    };
  }
  toggleMenu = () => {
    this.setState({ condition: true });
    this.setState({ showRowFilter: true });
  };
  toggleMenu2 = () => {
    this.setState({ condition: false });
    this.setState({ showRowCondition: false });
    this.setState({ showRowFilter: false });
    this.setState({ showRowCard: false });
    this.setState({ showRowFilter: false });
    this.setState({ showSearch: false });
  };

  showRow = () => {
    this.setState({ showRowCondition: true });
  };
  showRowCard = () => {
    this.setState({ showRowFilter: false });
    this.setState({ showRowCard: true });
  };
  showRowFilter = () => {
    this.setState({ showRowCard: false });
    this.setState({ showRowFilter: true });
  };
  handleChange = e => {
    this.setState({ search: e.target.value });
  };
  showSearch = () => {
    this.setState({ showSearch: true });
    this.setState({ showRowFilter: false });
    this.setState({ showRowCard: false });
  };
  sortByPriceAsc = () => {
    this.setState(prevState => {
      this.state.postsRow.sort((a, b) => a.id - b.id);
      this.setState({ showRowFilter: false });
    });
  };
  sortByPriceDesc = () => {
    this.setState(prevState => {
      this.state.postsRow.sort((a, b) => b.id - a.id);
      this.setState({ showRowFilter: false });
    });
  };

  getID = (e, id) => {
    const rowID = e.target.id;
    const p = this.state.postsRow[rowID];
    const n = p.Agent;
    const pName = p.Project;
    const t = p.Type;
    console.log("Name: " + n + " Project: " + pName + " Type: " + t);
  };

  render() {
    const { postsRow, postHead, search } = this.state;
    return (
      <Router>
        <div className="wrapper">
          <div className="table-responsive">
            <div className="row container-fluid above">
              <div className="col-6 text-left pt-3">
                <h5>Agents</h5>
              </div>
              <div className="col-6 text-right innerPageNav">
                <div className="">
                  <FontAwesomeIcon
                    icon="search"
                    size="lg"
                    color="#ddd"
                    onMouseEnter={this.showSearch}
                  />
                </div>
                <input
                  type="text"
                  className={
                    this.state.showSearch
                      ? "form-control filterInput show"
                      : "form-control filterInput hide"
                  }
                  onChange={this.handleChange}
                  onMouseLeave={this.toggleMenu2}
                  placeholder="Search..."
                  value={search}
                />
                <div className="">
                  <FontAwesomeIcon
                    icon="sort-amount-down"
                    size="lg"
                    color="#ddd"
                    onMouseEnter={this.showRowFilter}
                  />
                </div>
                <div className="">
                  <FontAwesomeIcon
                    icon="grip-horizontal"
                    size="lg"
                    color="#ddd"
                    onMouseEnter={this.showRowCard}
                  />
                </div>
              </div>
              <div className="col-12 text-right">
                <div
                  className={
                    this.state.showRowCard
                      ? "card tableToggleWrap"
                      : "card tableToggleWrap toggled"
                  }
                  onMouseLeave={this.toggleMenu2}
                >
                  <div className="card-body">
                    <ul>
                      <li
                        className="tableToggleCard"
                        onClick={this.toggleMenu2}
                      >
                        <FontAwesomeIcon
                          icon="th-large"
                          color="#495971"
                          size="xs"
                        />{" "}
                        Card
                      </li>
                      <li className="tableToggleRow" onClick={this.toggleMenu2}>
                        <FontAwesomeIcon
                          icon="table"
                          color="#495971"
                          size="xs"
                        />{" "}
                        Table
                      </li>
                    </ul>
                  </div>
                </div>

                <div
                  className={
                    this.state.showRowFilter
                      ? "card filter tableToggleWrap"
                      : "card filter tableToggleWrap toggled"
                  }
                  onMouseLeave={this.toggleMenu2}
                >
                  <div className="card-body">
                    <ul>
                      <li className="" onClick={this.sortByPriceAsc}>
                        <FontAwesomeIcon
                          icon="sort-alpha-down"
                          color="#495971"
                          size="1x"
                        />{" "}
                        ASC
                      </li>
                      <li className="" onClick={this.sortByPriceDesc}>
                        <FontAwesomeIcon
                          icon="sort-alpha-up"
                          color="#495971"
                          size="md"
                        />{" "}
                        DESC
                      </li>
                    </ul>
                  </div>
                </div>
              </div>
            </div>

            <Table
              Head={postHead.map(head => (
                <div className="col-2">{head.title}</div>
              ))}
              Body={postsRow.filter(searchFilter(search)).map(row => (
                <div className="rowData  col-12" key={row.id} id={row.id}>
                  <div className="col-2">
                    <div onClick={this.getID} id={row.id}>
                      {row.id}
                    </div>
                  </div>
                  <div className="col-2">
                    <Link
                      to={{
                        pathname: "/Agent_Edit",
                        state: {
                          id: row.id,
                          agent: row.Agent,
                          type: row.Type
                        }
                      }}
                      id={row.id}
                    >
                      <div>{row.Project}</div>
                    </Link>
                  </div>
                  <div className="col-2">
                    <div onClick={this.getID} id={row.id}>
                      {row.Agent}
                    </div>
                  </div>
                  <div className="col-2">
                    <div onClick={this.getID} id={row.id}>
                      {row.Type}
                    </div>
                  </div>
                </div>
              ))}
            />
          </div>
        </div>
        <Route path="/Agent_Edit" component={AgentEdit} />
      </Router>
    );
  }
}

export default Agents;
