import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import Logo from './img/Logo.png';

import Home from './Home';
import About from './About';
import Contact from './Contact';
import HomeOne from './home_sub/Home_One';
import HomeTwo from './home_sub/Home_Two';
import AboutOne from './about_sub/About_One';
import AboutTwo from './about_sub/About_Two';
import ContactOne from './contact_sub/Contact_One';
import ContactTwo from './contact_sub/Contact_Two';
import Messages from './components/Messages';
import User from './components/User';
var $  = require( 'jquery' );

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            condition: false,
            conditionMessage: false,
            conditionUser: false,
            isActive: 0,
            activeItem: 0,
            liSchedule: [
                {index: 1 , name:'Late', url:'/Late'},
                {index: 2 , name:'Due Within 1 Day ', url:'/One_Day'},
                {index: 3 , name:'7 Days Out ', url:'/7_Days'},
                {index: 4 , name:'14 Days Out ', url:'/14_Days'},
                {index: 5 , name:'New Commitment Date ', url:'/New_Commitment'},
                {index: 6 , name:'Completed Date ', url:'/Completed'},
            ],
            liSettings: [
                {index: 11 , name:'About 1', url:'/About_One'},
                {index: 22 , name:'About 2', url:'/About_Two'},
            ],
            NavText: 'Home'
        };

        this.toggleMenu = this.toggleMenu.bind(this);
        this.toggleMenu2 = this.toggleMenu2.bind(this);
        this.toggleMessage = this.toggleMessage.bind(this);
        this.toggleUser = this.toggleUser.bind(this);
        this.active = this.active.bind(this)
        this.LiItemClick = this.LiItemClick.bind(this)
    }

    toggleMenu() {
        this.setState({condition: true});
    }
    toggleMenu2() {
        this.setState({condition: false});
        this.setState({conditionMessage: false});
        this.setState({conditionUser: false});
    }
    toggleMessage() {
        this.setState({conditionMessage: true});
        this.setState({conditionUser: false});
    }
    toggleUser() {
        this.setState({conditionUser: true});
        this.setState({conditionMessage: false});
    }
    LiItemClick(index,e) {
        this.setState({activeItem: index})
        this.setState({ NavText: e.target.id });
    }
    active(index) {
        this.setState({ isActive: index });

    }
    componentDidMount(){
        $(function(){
            $('.homeInnerTab').addClass('active');
            $('.nav-link').click(function(){
                $('.fixed-top').addClass('toggled');
                $('#mainNavOverlay').addClass('hide');
                $('.homeInnerTab').removeClass('active');
            });
        });
        setTimeout(() => { 
            this.setState({loading: false})
          },2000)
    }
  render() {

    return (

        <Router>
        <header className="bg-light">
        <div>
            <div id="mainNavOverlay" className= { this.state.condition ?  'show' : "hide" }></div>
            <div className="row container-fluid">
                <div className="col-2 p-0">
                    <button type="button"  className="btn menuToggle" ref={button => {this.MainNavName = button;}} onMouseEnter={ this.toggleMenu } >
                    {this.state.NavText}
                    <div class="ec-menu-button">
                    <div></div>
                    <div style={{width: '20px'}}></div>
                    <div></div>
                </div>
                </button>
                </div>
                <div className="col-10">
                    <div className= { this.state.conditionMessage || this.state.conditionUser ? "mainTopNav" : "mainTopNav overflow" }>
                      <ul>
                          <li onMouseEnter={ this.toggleMessage }><FontAwesomeIcon icon="bell" color="#6199b4"/> Message<span className="line"></span>
                            <div className= { this.state.conditionMessage ? "card tableToggleWrap messageBox" : "card tableToggleWrap messageBox toggled" } onMouseLeave={ this.toggleMenu2 }>
                                <Messages/>
                            </div>
                          </li>
                          <li onMouseEnter={ this.toggleUser }><FontAwesomeIcon icon="smile" color="#6199b4"/> User<span className="line"></span>
                            <div className= { this.state.conditionUser ? "card tableToggleWrap messageBox userBox" : "card tableToggleWrap messageBox userBox toggled" } onMouseLeave={ this.toggleMenu2 }>
                                <User/>
                            </div>
                          </li>
                      </ul>
                      </div>
                </div>
            </div>
            <nav className= { this.state.condition ? " navbar-light bg-light fixed-top" : "navbar-light bg-light fixed-top toggled overflow" } onMouseLeave={ this.toggleMenu2 }>
                <ul className="nav" id="navbarNav">
                <li className="maninNavUlWrap" onClick={this.active.bind(this, 0)}>
                <Link to={'/Home'} id="Home" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'homeTopLi')}> Home  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                <Link to={'/Jobs'} id="Jobs" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'jobsTopLi')}> Jobs  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                <Link to={'/Dailies'} id="Dailies" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'dailiesTopLi')}> Dailies  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                <Link to={'/Orders'} id="Orders" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'ordersTopLi')}> Material Orders  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                <Link to={'/Crew'} id="Crew" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'crewTopLi')}> Crew  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                <Link to={'/Reports'} id="Reports" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'reportsTopLi')}> Reports  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                </li>

                <li className="maninNavUlWrap" onClick={this.active.bind(this, 1)}><Link to={'/Schedule'} id="Schedule" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'scheduleTopLi')}> Schedule  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                    <ul>
                    {this.state.liSchedule.map((liSchedule, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 1)}>
                        <Link to={liSchedule.url} id="Schedule" className={this.state.activeItem===liSchedule.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liSchedule.index)}>{liSchedule.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                <li className="maninNavUlWrap" onClick={this.active.bind(this, 2)}><Link to={'/Settings'} id="Settings" className='nav-link topLi' onClick={this.LiItemClick.bind(this, 'SettingsTopLi')}> Settings  <FontAwesomeIcon icon="angle-double-right" color="#6199b4" size="xs"/> </Link>
                <ul>
                    {this.state.liSettings.map((liSettings, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 2)}>
                        <Link to={liSettings.url} id="Settings" className={this.state.activeItem===liSettings.index ? 'nav-link active': 'nav-link'} onClick={this.LiItemClick.bind(this, liSettings.index)}>{liSettings.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                </ul>
            </nav>

            <div className="innerNav"> 

                <nav className={this.state.isActive===0  ? ' show': 'nav-link hide'}>
                <ul className="nav" id="navbarNav">
                <li className="innerNavName"><Link to={'/Home'} id="Home"  className={this.state.activeItem==='homeTopLi'  ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'homeTopLi')}> Home </Link></li>
                </ul>
                </nav>

                <nav className={this.state.isActive===0  ? ' show': 'nav-link hide'}>
                <ul className="nav" id="navbarNav">
                <li className="innerNavName"><Link to={'/Jobs'} id="Jobs"  className={this.state.activeItem==='jobsTopLi'  ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'jobsTopLi')}> Jobs </Link></li>
                </ul>
                </nav>

                <nav className={this.state.isActive===0  ? ' show': 'nav-link hide'}>
                <ul className="nav" id="navbarNav">
                <li className="innerNavName"><Link to={'/Dailies'} id="Dailies"  className={this.state.activeItem==='dailiesTopLi'  ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'dailiesTopLi')}> Dailies </Link></li>
                </ul>
                </nav>

                <nav className={this.state.isActive===0  ? ' show': 'nav-link hide'}>
                <ul className="nav" id="navbarNav">
                <li className="innerNavName"><Link to={'/Orders'} id="Orders"  className={this.state.activeItem==='ordersTopLi'  ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'ordersTopLi')}> Material Orders </Link></li>
                </ul>
                </nav>

                <nav className={this.state.isActive===0 ? ' show': 'nav-link hide'}>
                <ul className="nav" id="navbarNav">
                <li className="innerNavName"><Link to={'/Crew'} id="Crew"  className={this.state.activeItem==='crewTopLi'  ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'crewTopLi')}> Crew </Link></li>
                </ul>
                </nav>

                <nav className={this.state.isActive===0 ? ' show': 'nav-link hide'}>
                <ul className="nav" id="navbarNav">
                <li className="innerNavName"><Link to={'/Reports'} id="Reports"  className={this.state.activeItem==='reportsTopLi'  ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'reportsTopLi')}> Reports </Link></li>
                </ul>
                </nav>

                <nav className={this.state.isActive===1  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/Schedule'} id="Schedule"  className={this.state.activeItem==='scheduleTopLi'  ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'scheduleTopLi')}> Schedule </Link></li>
                            <ul>
                            {this.state.liSchedule.map((liSchedule, index) =>
                                <li key={index} onClick={this.active.bind(this, 0)}>
                                <Link to={liSchedule.url}  id="Schedule" className={this.state.activeItem===liSchedule.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liSchedule.index)}>{liSchedule.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
                <nav className={this.state.isActive===2  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/Settings'}id="Settings"   className={this.state.activeItem=== 'SettingsTopLi' ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'SettingsTopLi')}>Settings</Link></li>
                            <ul>
                            {this.state.liSettings.map((liSettings, index) =>
                                <li key={index} onClick={this.active.bind(this, 1)}>
                                <Link to={liSettings.url} id="Settings"  className={this.state.activeItem===liSettings.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liSettings.index)}>{liSettings.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
            </div>
      </div>
      <div className="logo"><img src={Logo} alt="Logo" width="120px"/></div>
      <div  className= { this.state.loading ?  'loaderWrapper show' : "loaderWrapper hide" }>
            <div className= 'loader'>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__ball"></div>
                <div className="loadingText">Loading Your Data...</div>
            </div>
        </div>
      </header>
      <Switch>
              <Route exact path='/Home' component={Home} />
              <Route path='/contact' component={Contact} />
              <Route path='/about' component={About} />
              <Route path='/Home_One' component={HomeOne} />
              <Route path='/Home_Two' component={HomeTwo} />
              <Route path='/About_One' component={AboutOne} />
              <Route path='/About_Two' component={AboutTwo} />
              <Route path='/Contact_One' component={ContactOne} />
              <Route path='/Contact_Two' component={ContactTwo} />
          </Switch>
      </Router>

    );
  }
}

export default Header;
