import React, { Component } from 'react';
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom';

import Home from './Home';
import About from './About';
import Contact from './Contact';
import HomeOne from './home_sub/Home_One';

class Header extends Component {
    constructor(props) {
        super(props);
        this.home = React.createRef();
        this.state = {
            condition: false,
            isActive: 0,
            activeItem: '',
            liHomes: [
                {index: 1 , name:'Home 1', url:'/Home_Page/Home_One'},
                {index: 2 , name:'Home 2', url:'/Home_Page/Home_One'},
            ],
            liAbouts: [
                {index: 1 , name:'About 1', url:'/Home_Page/Home_One'},
                {index: 2 , name:'About 2', url:'/Home_Page/Home_One'},
            ],
            liContacts: [
                {index: 1 , name:'Contact 1', url:'/Home_Page/Home_One'},
                {index: 2 , name:'Contact 2', url:'/Home_Page/Home_One'},
            ],
            NavText: 'Home'
        };

        this.toggleMenu = this.toggleMenu.bind(this);
        this.toggleMenu2 = this.toggleMenu2.bind(this);
        this.active = this.active.bind(this)
        this.LiItemClick = this.LiItemClick.bind(this)
    }

    toggleMenu() {
        this.setState({condition: true});
    }
    toggleMenu2() {
        this.setState({condition: false});
    }
    LiItemClick(e){
        const clicked = e.target.ref
        if(this.state.activeItem === clicked) { 
            this.setState({activeItem: ''});
        } else {
            this.setState({activeItem: clicked})
       }
    }
    active(index) {
        this.setState({ isActive: index });
        if(this.state.isActive===0){
            this.setState({NavText: "Home"})
        }
        if(this.state.isActive===1){
            this.setState({NavText: "About"})
        }
        if(this.state.isActive===2){
            this.setState({NavText: "Contact"})
        }
    }

  render() {

    return (

        <Router>
        <header className="bg-light">
        <div>
            <div id="mainNavOverlay" className= { this.state.condition ?  'show' : "hide" }></div>
            <button style={{position: 'relative',top: '6px',left: '10px',zIndex: '3'}} type="button"  className="btn menuToggle" ref={button => {this.MainNavName = button;}} onMouseEnter={ this.toggleMenu } >{this.state.NavText}</button>
            <nav className= { this.state.condition ? " navbar-light bg-light fixed-top" : "navbar-light bg-light fixed-top toggled" } onMouseLeave={ this.toggleMenu2 }>
                <ul className="nav" id="navbarNav">
                <li className="maninNavUlWrap"><Link to={'/'} className={this.state.isActive===0 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 0)} > Home </Link>
                    <ul>
                    {this.state.liHomes.map((liHome, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 0)}>
                        <Link ref={a => {this.home = a;}} to={liHome.url} className={this.state.activeItem=== 'home' ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick()}>{liHome.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                <li className="maninNavUlWrap"><Link to={'/about'} className={this.state.isActive===1 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 1)}> About </Link>
                <ul>
                    {this.state.liAbouts.map((liAbout, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 1)}>
                        <Link to={liAbout.url} className={this.state.activeItem===liAbout.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liAbout.index)}>{liAbout.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                <li className="maninNavUlWrap"><Link to={'/contact'} className={this.state.isActive===2 ? 'nav-link active': 'nav-link'}  onClick={this.active.bind(this, 2)}> Contact </Link>
                <ul>
                    {this.state.liContacts.map((liContact, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 2)}>
                        <Link to={liContact.url} className={this.state.activeItem===liContact.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liContact.index)}>{liContact.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                </ul>
            </nav>

            <div className="innerNav"> 
                <nav className={this.state.isActive===0  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/'} className='nav-link'> Home </Link></li>
                            <ul>
                            {this.state.liHomes.map((liHome, index) =>
                                <li key={index}>
                                <Link to={liHome.url} className={this.state.activeItem===liHome.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liHome.index)}>{liHome.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
                <nav className={this.state.isActive===1  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/about'} className='nav-link'>About</Link></li>
                            <ul>
                            {this.state.liAbouts.map((liAbout, index) =>
                                <li key={index}>
                                <Link to={liAbout.url} className={this.state.activeItem===liAbout.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liAbout.index)}>{liAbout.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
                <nav className={this.state.isActive===2  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/contact'} className='nav-link'>Contact</Link></li>
                            <ul>
                            {this.state.liContacts.map((liContact, index) =>
                                <li key={index}>
                                <Link to={liContact.url} className={this.state.activeItem===liContact.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liContact.index)}>{liContact.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
            </div>

      </div>
      </header>
      <Switch>
              <Route exact path='/' component={Home} />
              <Route path='/contact' component={Contact} />
              <Route path='/about' component={About} />
              <Route path='/Home_Page/Home_One' component={HomeOne} />
          </Switch>
      </Router>

    );
  }
}

export default Header;
