import ContactOne from './contact_sub/Contact_One';
import ContactTwo from './contact_sub/Contact_Two';
import Messages from './components/Messages';
import User from './components/User';
var $  = require( 'jquery' );

class Header extends Component {
    constructor(props) {
        super(props);
        this.state = {
            condition: false,
            conditionMessage: false,
            conditionUser: false,
            isActive: 0,
            activeItem: 0,
            prevScrollpos: window.pageYOffset,
            visible: true,
            liHomes: [
                {index: 1 , name:'Home 1', url:'/Home_One'},
                {index: 2 , name:'Home 2', url:'/Home_Two'},
            ],
            liAbouts: [
                {index: 11 , name:'About 1', url:'/About_One'},
                {index: 22 , name:'About 2', url:'/About_Two'},
            ],
            liContacts: [
                {index: 111 , name:'Contact 1', url:'/Contact_One'},
                {index: 222 , name:'Contact 2', url:'/Contact_Two'},
            ],
            NavText: 'Home'
        };

        this.toggleMenu = this.toggleMenu.bind(this);
        this.toggleMenu2 = this.toggleMenu2.bind(this);
        this.toggleMessage = this.toggleMessage.bind(this);
        this.toggleUser = this.toggleUser.bind(this);
        this.active = this.active.bind(this)
        this.LiItemClick = this.LiItemClick.bind(this)
    }

    toggleMenu() {
        this.setState({condition: true});
    }
    toggleMenu2() {
        this.setState({condition: false});
        this.setState({conditionMessage: false});
        this.setState({conditionUser: false});
    }
    toggleMessage() {
        this.setState({conditionMessage: true});
        this.setState({conditionUser: false});
    }
    toggleUser() {
        this.setState({conditionUser: true});
        this.setState({conditionMessage: false});
    }
    LiItemClick(index,e) {
        this.setState({activeItem: index})
        this.setState({ NavText: e.target.id });
    }
    active(index) {
        this.setState({ isActive: index });

    }
    componentDidMount(){
        $(function(){
            $('.homeInnerTab').addClass('active');
            $('.nav-link').click(function(){
                $('.fixed-top').addClass('toggled');
                $('#mainNavOverlay').addClass('hide');
                $('.homeInnerTab').removeClass('active');
            });
        });
        setTimeout(() => { 
            this.setState({loading: false})
          },2000)
    }
  render() {

    return (

        <Router>
        <header className={ this.state.condition ? "bg-light" : "bg-light  overflow" } >
        <div>
            <div id="mainNavOverlay" className= { this.state.condition ?  'show' : "hide" }></div>
            <div className="row container-fluid">
                <div className="col-2">
                    <button style={{position: 'relative',top: '6px',left: '0',zIndex: '3', border: 'solid thin #ddd',width: '200px'}} type="button"  className="btn menuToggle" ref={button => {this.MainNavName = button;}} onMouseEnter={ this.toggleMenu } >{this.state.NavText}<span className="line"></span></button>
                </div>
                <div className="col-10">
                    <div className= { this.state.conditionMessage || this.state.conditionUser ? "mainTopNav" : "mainTopNav overflow" }>
                      <ul>
                          <li onMouseEnter={ this.toggleMessage }><FontAwesomeIcon icon="bell" color="#7fb94b"/> Message<span className="line"></span>
                            <div className= { this.state.conditionMessage ? "card tableToggleWrap messageBox" : "card tableToggleWrap messageBox toggled" } onMouseLeave={ this.toggleMenu2 }>
                                <Messages/>
                            </div>
                          </li>
                          <li onMouseEnter={ this.toggleUser }><FontAwesomeIcon icon="smile" color="#7fb94b"/> User<span className="line"></span>
                            <div className= { this.state.conditionUser ? "card tableToggleWrap messageBox userBox" : "card tableToggleWrap messageBox userBox toggled" } onMouseLeave={ this.toggleMenu2 }>
                                <User/>
                            </div>
                          </li>
                      </ul>
                      </div>
                </div>
            </div>
            <nav className= { this.state.condition ? " navbar-light bg-light fixed-top" : "navbar-light bg-light fixed-top toggled " } onMouseLeave={ this.toggleMenu2 }>
                <ul className="nav" id="navbarNav">
                <li className="maninNavUlWrap" onClick={this.active.bind(this, 0)}><Link to={'/'} id="Home" className='nav-link topLi '  onClick={this.LiItemClick.bind(this, 'homeTopLi')}> Home  <FontAwesomeIcon icon="angle-double-right" color="#9a9a9a" size="xs"/> </Link>
                    <ul>
                    {this.state.liHomes.map((liHome, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 0)}>
                        <Link to={liHome.url} id="Home" className={this.state.activeItem===liHome.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liHome.index)}>{liHome.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                <li className="maninNavUlWrap" onClick={this.active.bind(this, 1)}><Link to={'/about'} id="About" className='nav-link topLi' onClick={this.LiItemClick.bind(this, 'aboutTopLi')}> About  <FontAwesomeIcon icon="angle-double-right" color="#9a9a9a" size="xs"/> </Link>
                <ul>
                    {this.state.liAbouts.map((liAbout, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 1)}>
                        <Link to={liAbout.url} id="About" className={this.state.activeItem===liAbout.index ? 'nav-link active': 'nav-link'} onClick={this.LiItemClick.bind(this, liAbout.index)}>{liAbout.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                <li className="maninNavUlWrap" onClick={this.active.bind(this, 2)}><Link to={'/contact'} id="Contact" className='nav-link topLi'  onClick={this.LiItemClick.bind(this, 'contactTopLi')}> Contact  <FontAwesomeIcon icon="angle-double-right" color="#9a9a9a" size="xs"/> </Link>
                <ul>
                    {this.state.liContacts.map((liContact, index) =>
                        <li
                        key={index}
                        onClick={this.active.bind(this, 2)}>
                        <Link to={liContact.url} id="Contact" className={this.state.activeItem===liContact.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liContact.index)}>{liContact.name}</Link>
                        </li>
                    )}
                    </ul>
                </li>
                </ul>
            </nav>

            <div className="innerNav"> 
                <nav className={this.state.isActive===0  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/'} id="Home"  className={this.state.activeItem==='homeTopLi'  ? 'nav-link active': 'homeInnerTab nav-link'}  onClick={this.LiItemClick.bind(this, 'homeTopLi')}> Home </Link></li>
                            <ul>
                            {this.state.liHomes.map((liHome, index) =>
                                <li key={index} onClick={this.active.bind(this, 0)}>
                                <Link to={liHome.url}  id="Home" className={this.state.activeItem===liHome.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liHome.index)}>{liHome.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
                <nav className={this.state.isActive===1  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/about'}id="About"   className={this.state.activeItem=== 'aboutTopLi' ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'aboutTopLi')}>About</Link></li>
                            <ul>
                            {this.state.liAbouts.map((liAbout, index) =>
                                <li key={index} onClick={this.active.bind(this, 1)}>
                                <Link to={liAbout.url} id="About"  className={this.state.activeItem===liAbout.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liAbout.index)}>{liAbout.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
                <nav className={this.state.isActive===2  ? ' show': 'nav-link hide'}>
                    <ul className="nav" id="navbarNav">
                        <li className="innerNavName"><Link to={'/contact'} id="Contact"  className={this.state.activeItem=== 'contactTopLi' ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, 'contactTopLi')}>Contact</Link></li>
                            <ul>
                            {this.state.liContacts.map((liContact, index) =>
                                <li key={index} onClick={this.active.bind(this, 2)}>
                                <Link to={liContact.url} id="Contact"  className={this.state.activeItem===liContact.index ? 'nav-link active': 'nav-link'}  onClick={this.LiItemClick.bind(this, liContact.index)}>{liContact.name}</Link>
                                </li>
                            )}
                            </ul>
                    </ul>
                </nav>
            </div>
      </div>
      <div className="logo"><img src={Logo} alt="Logo" width="120px"/></div>
      <div  className= { this.state.loading ?  'loaderWrapper show' : "loaderWrapper hide" }>
            <div className= 'loader'>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__bar"></div>
                <div className="loader__ball"></div>
                <div className="loadingText">Loading Your Data...</div>
            </div>
        </div>
      </header>
      <Switch>
              <Route exact path='/' component={Home} />
              <Route path='/contact' component={Contact} />
              <Route path='/about' component={About} />
              <Route path='/Home_One' component={HomeOne} />
              <Route path='/Home_Two' component={HomeTwo} />
              <Route path='/About_One' component={AboutOne} />
              <Route path='/About_Two' component={AboutTwo} />
              <Route path='/Contact_One' component={ContactOne} />
              <Route path='/Contact_Two' component={ContactTwo} />
          </Switch>
      </Router>

    );
  }
}

export default Header;
