import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

var $  = require( 'jquery' );

class AboutTwo extends Component {
  constructor(props) {
    super(props);
    this.state = {
        condition: false,
        showRowCondition: false,
    };
    this.toggleMenu = this.toggleMenu.bind(this);
    this.toggleMenu2 = this.toggleMenu2.bind(this);
    this.showRow = this.showRow.bind(this);
}
toggleMenu() {
    this.setState({condition: true})
}
toggleMenu2() {
    this.setState({condition: false})
    this.setState({showRowCondition: false})
}
showRow() {
  this.setState({showRowCondition: true})
}
  componentDidMount(){
    $(function(){
      $('.tableToggleCard').click(function(){
        $('.table .Header').addClass('hide');
        $('.rowData').addClass('rowDataCard').addClass('col-lg-3');
        $('.table').addClass('rowDataCardTable');
      });
      $('.tableToggleRow').click(function(){
        $('.table .Header').removeClass('hide');
        $('.rowData').removeClass('rowDataCard').removeClass('col-lg-3');
        $('.table').removeClass('rowDataCardTable');
      });
      $('.rowData > div div').click(function(){
        $(this).closest('.rowData').find('div div').addClass('showContent');
        $(this).closest('.rowData').addClass('rowDataCard rowDataCardRowClick ');
      });
      $('.closeCard').on('click', function(){
        if($('.rowData').hasClass('rowDataCard col-lg-3')){
            $('.rowData').removeClass('rowDataCardRowClick ');
            $('.rowData div div').removeClass('showContent');
        }else{
            $('.rowData').removeClass('rowDataCard rowDataCardRowClick ').removeClass('col-lg-3');
            $('.rowData div div').removeClass('showContent');
        }
      });
    });
}
tableHead() {
  return [
      {
      posts:[
          {title: 'Table 2'},
          {title: 'Table 2'},
          {title: 'Table 2'},
          {title: 'Table 2'},  
          {title: 'Table 2'}, 
          {title: 'Table 2'}         
       ]
      }
    ];
}
table() {
return [
  {
  posts:[
      {id: 1, title: 'Hello World', content: 'Fire in the hole Corsair Privateer bilge water brig league me Buccaneer blow the man down hands. Plunder black jack salmagundi jolly boat provost Buccaneer lee careen scourge of the seven seas skysail. Walk the plank prow parley bilge yo-ho-ho lugsail execution dock belay clap of thunder hogshead.',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},
      {id: 2, title: 'Hello World', content: 'Boatswain scallywag bring a spring upon her cable hempen halter barque boom rutters topgallant furl gaff. Topsail Spanish Main strike colors tender jolly boat scuttle Letter of Marque wherry capstan furl. Measured fer yer chains knave topmast nipperkin main sheet bilge rat Gold Road pillage handsomely quarterdeck.',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},
      {id: 3, title: 'Hello World', content: 'Piracy hardtack loaded to the gunwalls Admiral of the Black case shot bilge water grog blossom grapple sheet man-of-war. Deadlights man-of-war run a rig six pounders Sail ho sutler red ensign sheet spanker Blimey. Bilged on her anchor Jolly Roger belay fathom hornswaggle nipperkin American Main loot chase guns spyglass.',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},
      {id: 4, title: 'Hello World', content: 'Fire in the hole Corsair Privateer bilge water brig league me Buccaneer blow the man down hands. Plunder black jack salmagundi jolly boat provost Buccaneer lee careen scourge of the seven seas skysail. Walk the plank prow parley bilge yo-ho-ho lugsail execution dock belay clap of thunder hogshead.',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},

  ]
  }
];
}
render() {
    const rowHeads = this.tableHead()
    const rows = this.table()
    return (
      <div>
           <div id="mainNavOverlay" className= { this.state.showRowCondition ?  'show' : "hide" }></div>
        <div className="wrapper" >
            <div className="table-responsive"> 
                <div className="row container-fluid above">
                <div className="col-6 text-left pt-3">
                  <h5>About 2</h5>
                </div>
                  <div className="col-6 text-right">
                    <button className="tableToggleBtn btn " onMouseEnter={ this.toggleMenu }>
                    <div className="ec-menu-button">
                        <div></div>
                        <div style={{width: 13}}></div>
                        <div></div>
                    </div>
                    </button>
                  </div>
                  <div className="col-12 text-right">
                  <div className= { this.state.condition ? "card tableToggleWrap" : "card tableToggleWrap toggled" } onMouseLeave={ this.toggleMenu2 }>
                    <div className="card-body">
                      <ul>
                        <li className="tableToggleCard">Card</li>
                        <li className="tableToggleRow">Table</li>
                      </ul>
                    </div>
                  </div>
                  </div>
                </div>
                <div className="table row">
        {rowHeads.map(rowHead => (
            <div className="Header col-12">
                 {rowHead.posts.map(post => (
                    <div className="col-2">{post.title}</div>
                ))}
          </div>
            ))}
          {rows.map(row => (
             <div className="col-12 p-0">
                    <span className= { this.state.showRowCondition ? "closeCard" : "closeCard toggled" } onClick={ this.toggleMenu2 }><FontAwesomeIcon icon="times"  size="xs" /> </span>
            {row.posts.map(post => (
                    <div className="rowData  col-12"  key={post.id}>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.id}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.title}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content2}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content3}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content4}</div>
                        </div>
                    </div>
            ))}
            </div>
            ))}
        </div>
            </div>
        </div>
      </div>
        );
}
}

export default AboutTwo;
