import React, { Component } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'

var $  = require( 'jquery' );

class HomeOne extends Component {
  constructor(props) {
    super(props);
    this.state = {
        condition: false,
        showRowCondition: false,
    };
    this.toggleMenu = this.toggleMenu.bind(this);
    this.toggleMenu2 = this.toggleMenu2.bind(this);
    this.showRow = this.showRow.bind(this);
}
toggleMenu() {
    this.setState({condition: true})
}
toggleMenu2() {
    this.setState({condition: false})
    this.setState({showRowCondition: false})
}
showRow() {
  this.setState({showRowCondition: true})
}
  componentDidMount(){
    $(function(){
      $('.tableToggleCard').click(function(){
        $('.table .Header').addClass('hide');
        $('.rowData').addClass('rowDataCard').addClass('col-lg-3');
        $('.table').addClass('rowDataCardTable');
      });
      $('.tableToggleRow').click(function(){
        $('.table .Header').removeClass('hide');
        $('.rowData').removeClass('rowDataCard').removeClass('col-lg-3');
        $('.table').removeClass('rowDataCardTable');
      });
      $('.rowData > div div').click(function(){
        $(this).closest('.rowData').find('div div').addClass('showContent');
        $(this).closest('.rowData').addClass('rowDataCard rowDataCardRowClick ');
      });
      $('.closeCard').on('click', function(){
        $('.rowData').removeClass('rowDataCard rowDataCardRowClick ').removeClass('col-lg-3');
        $('.rowData div div').removeClass('showContent');
      });
    });
}
tableHead() {
  return [
      {
      posts:[
          {title: 'Hello World'},
          {title: 'Hello World'},
          {title: 'Hello World'},
          {title: 'Hello World'},  
          {title: 'Hello World'}, 
          {title: 'Hello World'}         
       ]
      }
    ];
}
table() {
return [
  {
  posts:[
      {id: 1, title: 'Hello World', content: 'Welcome to learning React!',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},
      {id: 2, title: 'Hello World', content: 'Welcome to learning React!',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},
      {id: 3, title: 'Hello World', content: 'Welcome to learning React!',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},
      {id: 4, title: 'Hello World', content: 'Welcome to learning React!',content2: 'Welcome to learning React!',content3: 'Welcome to learning React!',content4: 'Welcome to learning React!'},

  ]
  }
];
}
render() {
  const rowHeads = this.tableHead()
    const rows = this.table()
    return (
      <div>
           <div id="mainNavOverlay" className= { this.state.showRowCondition ?  'show' : "hide" }></div>
        <div className="wrapper">
            <div className="table-responsive"> 
                <div className="row container-fluid above">
                <div className="col-6 text-left pt-3">
                  <h5>Home 1</h5>
                </div>
                  <div className="col-6 text-right">
                  <FontAwesomeIcon icon="sort-amount-down"  size="2x" color="#ddd"/>
                    <button className="tableToggleBtn btn " onMouseEnter={ this.toggleMenu }>
                    <div className="ec-menu-button">
                        <div></div>
                        <div style={{width: 13}}></div>
                        <div></div>
                    </div>
                    </button>
                  </div>
                  <div className="col-12 text-right">
                  <div className= { this.state.condition ? "card tableToggleWrap" : "card tableToggleWrap toggled" } onMouseLeave={ this.toggleMenu2 }>
                    <div className="card-body">
                      <ul>
                        <li className="tableToggleCard">Card</li>
                        <li className="tableToggleRow">Table</li>
                      </ul>
                    </div>
                  </div>
                  </div>
                </div>
                <div className="table row">
        {rowHeads.map(rowHead => (
            <div className="Header col-12">
                 {rowHead.posts.map(post => (
                    <div className="col-2">{post.title}</div>
                ))}
          </div>
            ))}
          {rows.map(row => (
             <div className="col-12 p-0">
                    <span className= { this.state.showRowCondition ? "closeCard" : "closeCard toggled" } onClick={ this.toggleMenu2 }><FontAwesomeIcon icon="times"  size="xs" /> </span>
            {row.posts.map(post => (
                    <div className="rowData  col-12"  key={post.id}>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.id}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.title}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content2}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content3}</div>
                        </div>
                        <div className="col-2">
                            <div onClick={ this.showRow }>{post.content4}</div>
                        </div>
                    </div>
            ))}
            </div>
            ))}
        </div>
            </div>
        </div>
      </div>
        );
}
}

export default HomeOne;
